    @Override
    public void escribir(Citas citas) {
        txtNombre.setText(citas.getNombre());
        txtApellidoP.setText(citas.getApellidoPaterno());
        txtApellidoM.setText(citas.getApellidoMaterno());
        txtApellidoM.setText(citas.getApellidoMaterno());
        txtDia.setText(citas.getDia());
        txtMes.setText(citas.getMes());
        txtAnio.setText(citas.getAnio());
        txtHora.setText(citas.getHora());
        txtDescripcion.setText(citas.getDescripcion());
        for (int i = 0; i < comboConsultorios.getItemCount(); i++) {
            String item = comboConsultorios.getItemAt(i);
            if (item.equalsIgnoreCase(citas.getConsultorio())) {
                comboConsultorios.setSelectedIndex(i);
                break;
            }
        }
    }

    @Override
    public Citas modificar_citas() {
        Citas c = new Citas();
        c.setNombre(txtNombre.getText());
        c.setApellidoPaterno(txtApellidoP.getText());
        c.setApellidoMaterno(txtApellidoM.getText());
        c.setDia(txtDia.getText());
        c.setMes(txtMes.getText());
        c.setAnio(txtAnio.getText());
        c.setDescripcion(txtDescripcion.getText());
        c.setHora(txtHora.getText());
        c.setConsultorio(comboConsultorios.getSelectedItem().toString());
        return c;
    }

